Parameter.Value:
write(ConfigOutput24, 200);
if (Channel01enable=="on"){
	write(ConfigOutput01, 0x00
	 | ChannelFilter01=="on"       ? 0x10 : 0x00
	 | MinMaxCheck01=="positive"   ? 0x20 : 0x00
	 | MinMaxCheck01=="negative"   ? 0x20 : 0x00
	 | MinMaxCheck01=="both"       ? 0x20 : 0x00
	 | ChannelErrCheck01=="off"    ? 0x40 : 0x00
	 | ChannelType01=="0 to 20mA"  ? 0x03 : 0x00);
	write(ConfigOutput02, MinLimit01);
	write(ConfigOutput03, MaxLimit01);
	write(ConfigOutput04, UserGain01);
	write(ConfigOutput05, Useroffset01);
	if (ChannelFilter01=="on"){
		write(ConfigOutput26, FilterOrder01);
		write(ConfigOutput27, FilterConstant01);
	}
}else{
	write(ConfigOutput01, 0x80);
}
if (Channel02enable=="on"){
	write(ConfigOutput06, 0x00
	 | ChannelFilter02=="on"       ? 0x10 : 0x00
	 | MinMaxCheck02=="positive"   ? 0x20 : 0x00
	 | MinMaxCheck02=="negative"   ? 0x20 : 0x00
	 | MinMaxCheck02=="both"       ? 0x20 : 0x00
	 | ChannelErrCheck02=="off"    ? 0x40 : 0x00
	 | ChannelType02=="0 to 20mA"  ? 0x03 : 0x00);
	write(ConfigOutput07, MinLimit02);
	write(ConfigOutput08, MaxLimit02);
	write(ConfigOutput09, UserGain02);
	write(ConfigOutput10, Useroffset02);
	if (ChannelFilter02=="on"){
		write(ConfigOutput28, FilterOrder02);
		write(ConfigOutput29, FilterConstant02);
	}
}else{
	write(ConfigOutput06, 0x80);
}
write(ConfigOutput21, 0x00
 | TraceEdge=="negative"     ?  0x01 : 0x00
 | TraceEdge=="both"         ?  0x01 : 0x00
 | MinMaxCheck01=="negative" ?  0x10 : 0x00
 | MinMaxCheck01=="both"     ?  0x10 : 0x00
 | MinMaxCheck02=="negative" ?  0x20 : 0x00
 | MinMaxCheck02=="both"     ?  0x20 : 0x00);
write(ConfigOutput22, 0x00
 | TraceEdge=="positive"     ?  0x01 : 0x00
 | TraceEdge=="both"         ?  0x01 : 0x00
 | MinMaxCheck01=="positive" ?  0x10 : 0x00
 | MinMaxCheck01=="both"     ?  0x10 : 0x00
 | MinMaxCheck02=="positive" ?  0x20 : 0x00
 | MinMaxCheck02=="both"     ?  0x20 : 0x00);
if (TraceEnable=="on"){
	write(ConfigOutput25, 0x00
	 | TracePriority=="advanced" ?  0x06 : 0x03);
	write(TraceChannelEnable, 0x00
	 | TraceCh01enab=="on"     	?  0x01 : 0x00
	 | TraceCh02enab=="on"     	?  0x02 : 0x00);
	write(TraceSampleDepth, SampleCount);
	write(TraceTriggerStart, TriggerStartPos);
	write(TraceTriggerStop, TriggerStopPos);
	if (CompEnable=="on"){
		if (CompChannel =="Channel01"){
			write(cfgComp_EnableMask, 0x0101
			 | CompAddChannel == "Channel02" ? 0x0008 : 0x0101);
			if (CompTrigCond == "positive"){
				write(cfgComp_NominalState, 0x0001
				 | CompAddChannel == "Channel02" ? 0x0008 : 0x0001);
			}else{
				write(cfgComp_NominalState, 0x0100
				 | CompAddChannel == "Channel02" ? 0x0008 : 0x0100);
			}
			write(cfgComp_ConditionTypeMask, 0x0002);
			if (CompTrigDesc == "Init"){
				write(cfgComp_LowLimitCh01, LevelLow);
				write(cfgComp_HighLimitCh01, LevelHigh);
			}
			if(CompTrigAddDesc == "Init"){
				if (CompAddChannel =="Channel02"){
					write(cfgComp_LowLimitCh02, AddLevelLow);
					write(cfgComp_HighLimitCh02, AddLevelHigh);
				}
			}
		}
		if (CompChannel =="Channel02" ){
			write(cfgComp_EnableMask, 0x0404
			 | CompAddChannel == "Channel01" ? 0x0002 : 0x0404);
			if (CompTrigCond == "positive"){
				write(cfgComp_NominalState, 0x0004
				 | CompAddChannel == "Channel01" ? 0x0002 : 0x0004);
			}else{
				write(cfgComp_NominalState, 0x0400
				 | CompAddChannel == "Channel01" ? 0x0002 : 0x0400);
			}
			write(cfgComp_ConditionTypeMask, 0x0008);
			if (CompTrigDesc == "Init"){
				write(cfgComp_LowLimitCh02, LevelLow);
				write(cfgComp_HighLimitCh02, LevelHigh);
			}
			if(CompTrigAddDesc == "Init"){
				if (CompAddChannel =="Channel01"){
					write(cfgComp_LowLimitCh01, AddLevelLow);
					write(cfgComp_HighLimitCh01, AddLevelHigh);
				}
			}
		}
	}
}
write(ConfigOutput24, SampleTime);

write(ConfigOutput01, 0x0000
 | ChannelType01=="0 to 20mA" ? 0x0100 : 0x0000
 | ChannelType02=="0 to 20mA" ? 0x0200 : 0x0000);
if (Channel01Mode=="Validate"){
	write(Cfo_Channel01TimeMode, 0x0000
	 | ValTimeFormat=="32bit" ? 0x801f : 0x000f
	 | Channel01Time=="time1" ? 0x0100 : 0x0000);
}
if (Channel02Mode=="Validate"){
	write(Cfo_Channel02TimeMode, 0x0000
	 | ValTimeFormat=="32bit" ? 0x801f : 0x000f
	 | Channel02Time=="time1" ? 0x0100 : 0x0000);
}
